   159→  /**
   160→   * Helper to generate box-shadow CSS from a pattern
   161→   */
   162→  private generateBoxShadow(pattern: string[], pixelSize: number = 0.4): string {
   163→    let shadows: string[] = [];
   164→    pattern.forEach((row, y) => {
   165→      // Remove spaces to handle "X . X" formats if user adds extra spaces for readability
   166→      // But standard format is likely "X" or "." character by character?
   167→      // User said "string is an 8x8 string". Let's assume input is array of strings.
   168→      // We'll iterate characters.
   169→      const chars = row.replace(/\s+/g, '').split(''); // Remove whitespace delimiters if any
   170→
   171→      chars.forEach((char, x) => {
   172→        if (char === 'X') {
   173→          // x * pixelSize, y * pixelSize
   174→          shadows.push(`${x * pixelSize}vh ${y * pixelSize}vh 0 0 #CEFFFF`);
   175→        }
   176→      });
   177→    });
   178→    return shadows.join(',');
   179→  }
