     6→/**
     7→ * GameManager - Handles game lifecycle and menu transitions
     8→ */
     9→class GameManager {
    10→  private container: HTMLElement;
    11→  private mainMenu: MainMenu;
    12→  private game: Game | null = null;
    13→
    14→  constructor(container: HTMLElement) {
    15→    this.container = container;
    16→    this.mainMenu = new MainMenu(container);
    17→
    18→    // Set up menu callback
    19→    this.mainMenu.onStart((difficulty) => {
    20→      this.startGame(difficulty);
    21→    });
    22→
    23→    // Show menu initially
    24→    this.mainMenu.show();
    25→  }
    26→
    27→  /**
    28→   * Start a new game with selected difficulty
    29→   */
    30→  private startGame(difficulty: DifficultyLevel): void {
    31→    // Clean up existing game if any
    32→    if (this.game) {
    33→      this.game.dispose();
    34→      this.game = null;
    35→    }
    36→
    37→    // Create new game
    38→    this.game = new Game(this.container, difficulty, (victory, score, rank, breakdown) => {
    39→      this.handleGameOver(victory, score, rank, breakdown);
    40→    });
    41→    this.game.start();
    42→  }
    43→
    44→  /**
    45→   * Handle game over
    46→   */
    47→  private handleGameOver(
    48→    victory: boolean,
    49→    score: number,
    50→    rank: string,
    51→    breakdown: {
    52→      enemyScore: number;
    53→      timeBonus: number;
    54→      energyBonus: number;
    55→      starbasePenalty: number;
    56→    }
    57→  ): void {
    58→    this.mainMenu.showGameOver(victory, score, rank as any, breakdown, () => {
    59→      this.mainMenu.show();
    60→    });
    61→  }
    62→}
    63→
    64→// Initialize when DOM is ready
    65→document.addEventListener('DOMContentLoaded', () => {
    66→  const container = document.getElementById('game-container');
    67→  if (!container) {
    68→    console.error('Game container not found');
    69→    return;
    70→  }
    71→
    72→  new GameManager(container);
    73→});
