     1→import { DifficultyLevel } from '../game/GameState';
     2→
     3→/**
     4→ * SectorSystem - Manages the 8x8 galactic grid and sector contents
     5→ */
     6→
     7→export interface SectorData {
     8→  x: number;
     9→  y: number;
    10→  enemies: number; // Count of enemies in sector
    11→  hasStarbase: boolean;
    12→  starbaseDestroyed: boolean;
    13→  visited: boolean;
    14→}
    15→
    16→export class SectorSystem {
    17→  public static readonly GRID_SIZE = 8;
    18→  private sectors: SectorData[][] = [];
    19→  private totalEnemies: number = 0;
    20→  private totalStarbases: number = 0;
    21→
    22→  constructor() {
    23→    this.initializeGrid();
    24→  }
    25→
    26→  /**
    27→   * Initialize empty grid
    28→   */
    29→  private initializeGrid(): void {
    30→    this.sectors = [];
    31→    for (let y = 0; y < SectorSystem.GRID_SIZE; y++) {
    32→      this.sectors[y] = [];
    33→      for (let x = 0; x < SectorSystem.GRID_SIZE; x++) {
    34→        this.sectors[y][x] = {
    35→          x,
    36→          y,
    37→          enemies: 0,
    38→          hasStarbase: false,
    39→          starbaseDestroyed: false,
    40→          visited: false,
    41→        };
    42→      }
    43→    }
    44→  }
    45→
    46→  /**
    47→   * Generate galaxy based on difficulty level
    48→   */
    49→  public generateGalaxy(difficulty: DifficultyLevel): void {
    50→    this.initializeGrid();
    51→
    52→    // Determine counts based on difficulty
    53→    let enemyCount: number;
    54→    let starbaseCount: number;
    55→
    56→    switch (difficulty) {
    57→      case DifficultyLevel.NOVICE:
    58→        enemyCount = 24;
    59→        starbaseCount = 3;
    60→        break;
    61→      case DifficultyLevel.PILOT:
    62→        enemyCount = 36;
    63→        starbaseCount = 3;
    64→        break;
    65→      case DifficultyLevel.WARRIOR:
    66→        enemyCount = 45;
    67→        starbaseCount = 3;
    68→        break;
    69→      case DifficultyLevel.COMMANDER:
    70→        enemyCount = 60;
    71→        starbaseCount = 4;
    72→        break;
    73→      default:
    74→        enemyCount = 24;
    75→        starbaseCount = 3;
    76→    }
    77→
    78→    this.totalEnemies = enemyCount;
    79→    this.totalStarbases = starbaseCount;
    80→
    81→    // Place starbases first (spread them out)
    82→    this.placeStarbases(starbaseCount);
    83→
    84→    // Place enemies (avoid player starting position)
    85→    this.placeEnemies(enemyCount);
    86→
    87→    // Mark starting sector as visited
    88→    this.sectors[4][4].visited = true;
    89→  }
    90→
    91→  /**
    92→   * Place starbases spread across the galaxy
    93→   */
    94→  private placeStarbases(count: number): void {
    95→    const positions: [number, number][] = [];
    96→
    97→    // Divide galaxy into regions for even distribution
    98→    const regions = [
    99→      { minX: 0, maxX: 3, minY: 0, maxY: 3 },
   100→      { minX: 4, maxX: 7, minY: 0, maxY: 3 },
   101→      { minX: 0, maxX: 3, minY: 4, maxY: 7 },
   102→      { minX: 4, maxX: 7, minY: 4, maxY: 7 },
   103→    ];
   104→
   105→    for (let i = 0; i < count && i < regions.length; i++) {
   106→      const region = regions[i];
   107→      let x: number, y: number;
   108→      let attempts = 0;
   109→
   110→      do {
   111→        x = region.minX + Math.floor(Math.random() * (region.maxX - region.minX + 1));
   112→        y = region.minY + Math.floor(Math.random() * (region.maxY - region.minY + 1));
   113→        attempts++;
   114→      } while ((x === 4 && y === 4) && attempts < 20); // Avoid player start
   115→
   116→      if (!positions.some(([px, py]) => px === x && py === y)) {
   117→        positions.push([x, y]);
   118→        this.sectors[y][x].hasStarbase = true;
   119→      }
   120→    }
   121→  }
   122→
   123→  /**
   124→   * Place enemies across the galaxy
   125→   */
   126→  private placeEnemies(count: number): void {
   127→    let placed = 0;
   128→    const maxPerSector = 4;
   129→    const attempts = count * 10;
   130→    let attempt = 0;
   131→
   132→    while (placed < count && attempt < attempts) {
   133→      const x = Math.floor(Math.random() * SectorSystem.GRID_SIZE);
   134→      const y = Math.floor(Math.random() * SectorSystem.GRID_SIZE);
   135→
   136→      // Skip player starting position initially
   137→      if (x === 4 && y === 4) {
   138→        attempt++;
   139→        continue;
   140→      }
   141→
   142→      // Check if sector can hold more enemies
   143→      if (this.sectors[y][x].enemies < maxPerSector) {
   144→        this.sectors[y][x].enemies++;
   145→        placed++;
   146→      }
   147→
   148→      attempt++;
   149→    }
   150→  }
   151→
   152→  /**
   153→   * Get sector data
   154→   */
   155→  public getSector(x: number, y: number): SectorData | null {
   156→    if (x < 0 || x >= SectorSystem.GRID_SIZE || y < 0 || y >= SectorSystem.GRID_SIZE) {
   157→      return null;
   158→    }
   159→    return this.sectors[y][x];
   160→  }
   161→
   162→  /**
   163→   * Get all sectors
   164→   */
   165→  public getAllSectors(): SectorData[][] {
   166→    return this.sectors;
   167→  }
   168→
   169→  /**
   170→   * Mark sector as visited
   171→   */
   172→  public visitSector(x: number, y: number): void {
   173→    const sector = this.getSector(x, y);
   174→    if (sector) {
   175→      sector.visited = true;
   176→    }
   177→  }
   178→
   179→  /**
   180→   * Remove an enemy from a sector
   181→   */
   182→  public removeEnemy(x: number, y: number): boolean {
   183→    const sector = this.getSector(x, y);
   184→    if (sector && sector.enemies > 0) {
   185→      sector.enemies--;
   186→      this.totalEnemies--;
   187→      return true;
   188→    }
   189→    return false;
   190→  }
   191→
   192→  /**
   193→   * Destroy a starbase
   194→   */
   195→  public destroyStarbase(x: number, y: number): boolean {
   196→    const sector = this.getSector(x, y);
   197→    if (sector && sector.hasStarbase && !sector.starbaseDestroyed) {
   198→      sector.starbaseDestroyed = true;
   199→      this.totalStarbases--;
   200→      return true;
   201→    }
   202→    return false;
   203→  }
   204→
   205→  /**
   206→   * Check if a starbase is under attack (enemies in same sector)
   207→   */
   208→  public isStarbaseUnderAttack(x: number, y: number): boolean {
   209→    const sector = this.getSector(x, y);
   210→    return sector !== null && sector.hasStarbase && !sector.starbaseDestroyed && sector.enemies > 0;
   211→  }
   212→
   213→  /**
   214→   * Get list of starbases under attack
   215→   */
   216→  public getStarbasesUnderAttack(): SectorData[] {
   217→    const underAttack: SectorData[] = [];
   218→    for (let y = 0; y < SectorSystem.GRID_SIZE; y++) {
   219→      for (let x = 0; x < SectorSystem.GRID_SIZE; x++) {
   220→        if (this.isStarbaseUnderAttack(x, y)) {
   221→          underAttack.push(this.sectors[y][x]);
   222→        }
   223→      }
   224→    }
   225→    return underAttack;
   226→  }
   227→
   228→  /**
   229→   * Get remaining enemy count
   230→   */
   231→  public getRemainingEnemies(): number {
   232→    return this.totalEnemies;
   233→  }
   234→
   235→  /**
   236→   * Get remaining starbase count
   237→   */
   238→  public getRemainingStarbases(): number {
   239→    return this.totalStarbases;
   240→  }
   241→
   242→  /**
   243→   * Get sector symbol for chart display
   244→   */
   245→  public getSectorSymbol(x: number, y: number): string {
   246→    const sector = this.getSector(x, y);
   247→    if (!sector) return ' ';
   248→
   249→    // Starbase indicators take priority
   250→    if (sector.hasStarbase && !sector.starbaseDestroyed) {
   251→      if (sector.enemies > 0) {
   252→        return '\u2265*'; // Starbase under attack
   253→      }
   254→      return '*'; // Friendly starbase
   255→    }
   256→
   257→    // Enemy indicators
   258→    switch (sector.enemies) {
   259→      case 0:
   260→        return ' ';
   261→      case 1:
   262→        return '<';
   263→      case 2:
   264→        return '=';
   265→      case 3:
   266→        return '>';
   267→      default:
   268→        return '\u2265'; // Fleet (4+)
   269→    }
   270→  }
   271→}
