    13→
    14→/**
    15→ * StarbaseAttackSystem - Manages strategic enemy behavior to surround and destroy starbases
    16→ *
    17→ * Strategy:
    18→ * 1. Select one starbase as the current attack target
    19→ * 2. Move enemies from nearby sectors to the 4 adjacent sectors (up/down/left/right)
    20→ * 3. Once all 4 adjacent sectors have enemies, the starbase is "surrounded"
    21→ * 4. After 5 minutes of being surrounded, the starbase is destroyed
    22→ * 5. Only one starbase can be targeted at a time
    23→ */
    24→export class StarbaseAttackSystem {
    25→  private sectorSystem: SectorSystem;
    26→  private currentAttack: StarbaseAttackState | null = null;
    27→  private difficulty: DifficultyLevel;
    28→
    29→  // Timer constants
    30→  private static readonly DESTRUCTION_TIME = 300; // 5 minutes in seconds
    31→  private static readonly MOVEMENT_INTERVAL = 30; // Seconds between enemy movements
    32→
    33→  private lastMovementTime: number = 0;
    34→  private gameTime: number = 0;
    35→
    36→  // Callback for broadcasting messages
    37→  private onMessage: ((message: string, duration?: number) => void) | null = null;
    38→
    39→  constructor(sectorSystem: SectorSystem, difficulty: DifficultyLevel) {
    40→    this.sectorSystem = sectorSystem;
    41→    this.difficulty = difficulty;
    42→  }
    43→
    44→  /**
    45→   * Set the message callback for broadcasting alerts
    46→   */
    47→  public setMessageCallback(callback: (message: string, duration?: number) => void): void {
    48→    this.onMessage = callback;
    49→  }
    50→
    51→  /**
    52→   * Broadcast a message to the player
    53→   */
    54→  private broadcast(message: string, duration?: number): void {
    55→    if (this.onMessage) {
    56→      this.onMessage(message, duration);
    57→    }
    58→  }
    59→
    60→  /**
    61→   * Get the 4 adjacent sectors (up, down, left, right) for a given position
    62→   */
    63→  private getAdjacentSectors(x: number, y: number): { x: number; y: number }[] {
    64→    const adjacent: { x: number; y: number }[] = [];
    65→    const directions = [
    66→      { dx: 0, dy: -1 }, // Up
    67→      { dx: 0, dy: 1 },  // Down
    68→      { dx: -1, dy: 0 }, // Left
    69→      { dx: 1, dy: 0 },  // Right
    70→    ];
    71→
    72→    for (const dir of directions) {
    73→      const nx = x + dir.dx;
    74→      const ny = y + dir.dy;
    75→      // Check bounds
    76→      if (nx >= 0 && nx < SectorSystem.GRID_SIZE && ny >= 0 && ny < SectorSystem.GRID_SIZE) {
    77→        adjacent.push({ x: nx, y: ny });
    78→      }
    79→    }
    80→
    81→    return adjacent;
    82→  }
    83→
    84→  /**
    85→   * Check if a starbase at the given position is surrounded
    86→   * A starbase is surrounded when all 4 adjacent sectors have at least 1 enemy
    87→   */
    88→  private isStarbaseSurrounded(x: number, y: number): boolean {
    89→    const adjacent = this.getAdjacentSectors(x, y);
    90→
    91→    // If starbase is at edge/corner, it has fewer than 4 adjacent sectors
    92→    // In that case, ALL available adjacent sectors must have enemies
    93→    for (const pos of adjacent) {
    94→      const sector = this.sectorSystem.getSector(pos.x, pos.y);
    95→      if (!sector || sector.enemies === 0) {
    96→        return false;
    97→      }
    98→    }
    99→
   100→    return adjacent.length > 0;
   101→  }
   102→
   103→  /**
   104→   * Find the best starbase to target for attack
   105→   * Prioritizes starbases that are:
   106→   * 1. Not already destroyed
   107→   * 2. Have enemies in nearby sectors (easier to surround)
   108→   */
   109→  private selectTargetStarbase(): { x: number; y: number } | null {
   110→    const sectors = this.sectorSystem.getAllSectors();
   111→    let bestTarget: { x: number; y: number; score: number } | null = null;
   112→
   113→    for (let y = 0; y < SectorSystem.GRID_SIZE; y++) {
   114→      for (let x = 0; x < SectorSystem.GRID_SIZE; x++) {
   115→        const sector = sectors[y][x];
   116→
   117→        if (sector.hasStarbase && !sector.starbaseDestroyed) {
   118→          // Calculate attack score based on nearby enemy presence
   119→          let score = 0;
   120→          const adjacent = this.getAdjacentSectors(x, y);
   121→
   122→          for (const pos of adjacent) {
   123→            const adjSector = this.sectorSystem.getSector(pos.x, pos.y);
   124→            if (adjSector && adjSector.enemies > 0) {
   125→              score += adjSector.enemies;
   126→            }
   127→          }
   128→
   129→          // Also consider enemies within 2 sectors (reinforcement potential)
   130→          for (let dy = -2; dy <= 2; dy++) {
   131→            for (let dx = -2; dx <= 2; dx++) {
   132→              if (Math.abs(dx) + Math.abs(dy) === 2) { // Manhattan distance 2
   133→                const nearSector = this.sectorSystem.getSector(x + dx, y + dy);
   134→                if (nearSector && nearSector.enemies > 0) {
   135→                  score += nearSector.enemies * 0.5;
   136→                }
   137→              }
   138→            }
   139→          }
   140→
   141→          if (!bestTarget || score > bestTarget.score) {
   142→            bestTarget = { x, y, score };
   143→          }
   144→        }
   145→      }
   146→    }
   147→
   148→    return bestTarget ? { x: bestTarget.x, y: bestTarget.y } : null;
   149→  }
   150→
   151→  /**
   152→   * Move one enemy from a source sector to a target sector
   153→   */
   154→  private moveEnemy(fromX: number, fromY: number, toX: number, toY: number): boolean {
   155→    const fromSector = this.sectorSystem.getSector(fromX, fromY);
