   158→    if (!fromSector || !toSector || fromSector.enemies === 0) {
   159→      return false;
   160→    }
   161→
   162→    // Move enemy: decrement source, increment destination
   163→    fromSector.enemies--;
   164→    toSector.enemies++;
   165→
   166→    return true;
   167→  }
   168→
   169→  /**
   170→   * Execute strategic enemy movements to surround the target starbase
